import React, { useEffect, useState } from "react";
import axios from "axios";
import "../css/admin.css";

const AdminPanel = () => {
  const [poruke, setPoruke] = useState([]);
  const [naziv, setNaziv] = useState("");
  const [opis, setOpis] = useState("");
  const [gramaza, setGramaza] = useState("");
  const [cijena, setCijena] = useState("");
  const [slika, setSlika] = useState("");

  const [svi, setSvi] = useState([]);
  const [preporuke, setPreporuke] = useState([]);
  const [best, setBest] = useState([]);

  const loadMessages = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/poruke");
      setPoruke(res.data);
    } catch (error) {
      console.error("Greška pri učitavanju poruka:", error);
    }
  };

  const loadData = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/products");
      setSvi(Array.isArray(res.data.svi) ? res.data.svi : []);
      setPreporuke(Array.isArray(res.data.preporuke) ? res.data.preporuke : []);
      setBest(Array.isArray(res.data.best) ? res.data.best : []);
    } catch (error) {
      alert("Greška pri učitavanju podataka.");
      console.error(error);
    }
  };

  const obrisiPoruku = (index) => {
    setPoruke((prev) => prev.filter((_, i) => i !== index));
  };

  useEffect(() => {
    loadData();
    loadMessages();
  }, []);

  const handleAddNew = async (e) => {
    e.preventDefault();

    if (!naziv || !opis || !gramaza || !cijena || !slika) {
      alert("Molimo popunite sva polja.");
      return;
    }

    const noviProizvod = { naziv, opis, gramaza, cijena, slika };

    try {
      await axios.post("http://localhost:5000/api/products/svi", noviProizvod);
      alert("Proizvod dodat u 'svi'.");
      setNaziv("");
      setOpis("");
      setGramaza("");
      setCijena("");
      setSlika("");
      loadData();
    } catch (error) {
      alert("Greška pri dodavanju proizvoda.");
      console.error(error);
    }
  };

  const addToCategory = async (proizvod, kategorija) => {
    try {
      await axios.put(`http://localhost:5000/api/dodaj/${kategorija}`, proizvod);
      loadData();
    } catch (error) {
      alert(error.response?.data?.error || "Greška pri dodavanju u kategoriju.");
    }
  };

  const deleteFromCategory = async (kategorija, naziv) => {
    try {
      await axios.delete(`http://localhost:5000/api/obrisi/${kategorija}/${naziv}`);
      loadData();
    } catch (error) {
      alert("Greška pri brisanju proizvoda.");
      console.error(error);
    }
  };

  return (
    <div className="admin-panel">
      <h1 className="admin-title">Admin panel - Dodaj novu kafu</h1>
      <form onSubmit={handleAddNew} className="admin-form">
        <input
          type="text"
          placeholder="Naziv"
          value={naziv}
          onChange={(e) => setNaziv(e.target.value)}
          className="admin-input"
        />
        <textarea
          placeholder="Opis"
          value={opis}
          onChange={(e) => setOpis(e.target.value)}
          className="admin-textarea"
        />
        <input
          type="text"
          placeholder="Gramaža (npr. 250g)"
          value={gramaza}
          onChange={(e) => setGramaza(e.target.value)}
          className="admin-input"
        />
        <input
          type="text"
          placeholder="Cijena (npr. $12.50)"
          value={cijena}
          onChange={(e) => setCijena(e.target.value)}
          className="admin-input"
        />
        <input
          type="text"
          placeholder="Putanja do slike"
          value={slika}
          onChange={(e) => setSlika(e.target.value)}
          className="admin-input"
        />
        <button type="submit" className="admin-button">
          Dodaj u 'svi'
        </button>
      </form>

      <div className="admin-container">
        <section className="admin-section">
          <h2>Preporuke (max 4)</h2>
          <div className="kafa-list">
            {preporuke.length === 0 && <p>Nema proizvoda.</p>}
            {preporuke.map((p) => (
              <div key={p.naziv} className="kafa">
                <span>{p.naziv}</span>
                <div className="kafa-buttons">
                  <button
                    className="btn-delete"
                    onClick={() => deleteFromCategory("preporuke", p.naziv)}
                  >
                    Obriši
                  </button>
                </div>
              </div>
            ))}
          </div>
        </section>

        <section className="admin-section">
          <h2>Best (max 4)</h2>
          <div className="kafa-list">
            {best.length === 0 && <p>Nema proizvoda.</p>}
            {best.map((p) => (
              <div key={p.naziv} className="kafa">
                <span>{p.naziv}</span>
                <div className="kafa-buttons">
                  <button
                    className="btn-delete"
                    onClick={() => deleteFromCategory("best", p.naziv)}
                  >
                    Obriši
                  </button>
                </div>
              </div>
            ))}
          </div>
        </section>

        <section className="admin-section">
          <h2>Svi proizvodi</h2>
          <div className="kafa-list">
            {svi.length === 0 && <p>Nema proizvoda.</p>}
            {svi.map((p) => (
              <div key={p.naziv} className="kafa">
                <span>{p.naziv}</span>
                <div className="kafa-buttons">
                  <button
                    className="btn-add"
                    onClick={() => addToCategory(p, "preporuke")}
                    disabled={preporuke.length >= 4}
                    title={preporuke.length >= 4 ? "Preporuke su pune" : ""}
                  >
                    Dodaj u preporuke
                  </button>
                  <button
                    className="btn-add"
                    onClick={() => addToCategory(p, "best")}
                    disabled={best.length >= 4}
                    title={best.length >= 4 ? "Best kategorija je puna" : ""}
                  >
                    Dodaj u best
                  </button>
                  <button
                    className="btn-delete"
                    onClick={() => deleteFromCategory("svi", p.naziv)}
                  >
                    Obriši
                  </button>
                </div>
              </div>
            ))}
          </div>
        </section>
      </div>

      <div className="user-messages">
        <h2>Poruke korisnika</h2>
        {poruke.length === 0 ? (
          <p>Nema poruka.</p>
        ) : (
          <div className="messages-list">
            {poruke.map((msg, index) => (
              <div key={index} className="message-card">
                <p>
                  <strong>Ime:</strong> {msg.ime || "Nepoznato"}
                </p>
                <p>
                  <strong>Email:</strong> {msg.email || "Nepoznat"}
                </p>
                <p>
                  <strong>Poruka:</strong> {msg.poruka || "Prazno"}
                </p>
                <button className="btn-delete" onClick={() => obrisiPoruku(index)}>
                  Obriši
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminPanel;
