import React, { useEffect, useState } from "react";

const Messages = () => {
  const [poruke, setPoruke] = useState([]);

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (!user || user.role !== "admin") {
      window.location.href = "/";
      return;
    }

    fetch("http://localhost:5000/api/poruke")
      .then((res) => res.json())
      .then((data) => setPoruke(data))
      .catch((err) => console.error("Greška:", err));
  }, []);

  return (
    <div style={{ padding: "2rem" }}>
      <h2>Primljene poruke</h2>
      {poruke.length === 0 ? (
        <p>Nema poruka.</p>
      ) : (
        poruke.map((p, i) => (
          <div key={i} style={{ border: "1px solid #ccc", padding: "1rem", marginBottom: "1rem" }}>
            <p><strong>Ime:</strong> {p.ime}</p>
            <p><strong>Email:</strong> {p.email}</p>
            <p><strong>Poruka:</strong> {p.poruka}</p>
          </div>
        ))
      )}
    </div>
  );
};

export default Messages;
